private static org.apache.ecs.html.Option makeOption(String line) {
    StringTokenizer st = new StringTokenizer(line, "|");
    org.apache.ecs.html.Option o = new org.apache.ecs.html.Option();
    String token = "";
    if (st.hasMoreTokens()) {
        token = st.nextToken();
    }
    o.addElement(token);
    return (o);
}

