    if (hintNum < maxHints - 1) {
        hintNum++;
        getCurrentLesson().getLessonTracker(this).setMaxHintLevel(getHintNum() + 1);
        hint = (String)getCurrentLesson().getHint(this, getHintNum());
    }
    return hint;
}

